import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import jwt_decode from 'jwt-decode'; // Import jwt-decode
import { RootState } from '../../app/store';
import { login } from './loginAPI';

export interface loginState {
  username: string;
  password: string;
  status: 'idle' | 'loading' | 'failed';
  token: string;
  logged: boolean;
  firstName?: string;
  lastName?: string;
}

const initialState: loginState = {
  username: '',
  password: '',
  status: 'idle',
  token: '',
  logged: false,
  firstName: undefined,
  lastName: undefined,
};

export const loginAsync = createAsyncThunk(
  'login/login',
  async (credentials: { username: string; password: string }) => {
    const response = await login(credentials);
    return response.data;
  }
);

export const loginSlice = createSlice({
  name: 'login',
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(loginAsync.fulfilled, (state, action) => {
        const decodedToken = jwt_decode(action.payload.access);

        state.token = action.payload.access;
        state.logged = true;
        state.status = 'loading';
        state.firstName = decodedToken.first_name;
        state.lastName = decodedToken.last_name;

        // Store the token and additional data in sessionStorage
        sessionStorage.setItem('token', JSON.stringify(state.token));
        sessionStorage.setItem('firstName', JSON.stringify(state.firstName));
        sessionStorage.setItem('lastName', JSON.stringify(state.lastName));
      })
      .addCase(loginAsync.rejected, (state, action) => {
        if (action.error.message === 'Token expired') {
          state.token = '';
          state.logged = false;
          state.status = 'idle';
          state.firstName = undefined;
          state.lastName = undefined;

          sessionStorage.removeItem('token');
          sessionStorage.removeItem('firstName');
          sessionStorage.removeItem('lastName');
        }
      });
  },
});

export const selectstatus = (state: RootState) => state.login.status;
export const selectLogged = (state: RootState) => state.login.logged;
export const selectToken = (state: RootState) => state.login.token;
export const selectFirstName = (state: RootState) => state.login.firstName;
export const selectLastName = (state: RootState) => state.login.lastName;

export default loginSlice.reducer;
