// src/services/appointmentsApi.ts
import axios, { AxiosRequestConfig } from 'axios';



const MY_SERVER = 'http://127.0.0.1:8000/appointments/';
;
const getAppointments = async () => {
  try {


    const myToken = sessionStorage.getItem('token'); // Retrieve the token from session storage
    console.log(myToken);

    if (!myToken) {
      // Handle the case where the token is not available
      throw new Error('Authentication token not found in session storage');
    }

    const config: AxiosRequestConfig = {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    };

    const response = await axios.get(MY_SERVER, config);
    return response.data;


  } catch (error) {
    // Handle error
    console.error('Error fetching appointments:', error);
    throw error;
  }
};

export default getAppointments;
