import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { ThunkDispatch } from 'redux-thunk';
import { AnyAction } from 'redux';
import { fetchAppointments, selectAppointments, selectAppointmentsStatus, selectAppointmentsError } from './appointmentSlice';
import { selectFirstName, selectToken } from '../login/loginSlice';
import getAppointments from './appointmentAPI';

const Appointment: React.FC = () => {
  const dispatch = useDispatch<ThunkDispatch<any, void, AnyAction>>(); // Explicitly type dispatch

  const appointments = useSelector(selectAppointments);
  const status = useSelector(selectAppointmentsStatus);
  const error = useSelector(selectAppointmentsError);
  const userFirstName = useSelector(selectFirstName);
  const token = useSelector(selectToken);

  useEffect(() => {
    // Dispatch the fetchAppointments action when the component mounts
    dispatch(fetchAppointments());
  }, [dispatch]);

  if (status === 'loading') {
    return <div>Loading...</div>;
  }

  if (status === 'failed') {
    return <div>Error: {error}</div>;
  }

  // Helper function to group appointments by day of the week
  const groupAppointmentsByDay = () => {
    const groupedAppointments = {};
    appointments.forEach((appointment) => {
      const dayOfWeek = appointment.day_of_week;
      if (!groupedAppointments[dayOfWeek]) {
        groupedAppointments[dayOfWeek] = [];
      }
      groupedAppointments[dayOfWeek].push(appointment);
    });
    return groupedAppointments;
  };

  const renderWeeklyCalendar = () => {
    const groupedAppointments = groupAppointmentsByDay();

    return (
      <div>
        <h1>Weekly Calendar</h1>
        {Object.entries(groupedAppointments).map(([dayOfWeek, appointmentsForDay]) => (
          <div key={dayOfWeek}>
            <h2>{dayOfWeek}</h2>
            {appointmentsForDay.map((appointment, index) => (
              <div key={index}>
                <p>Therapist: {appointment.therapist.username}</p>
                <p>
                  Patient: {appointment.patient.first_name} {appointment.patient.last_name}
                </p>
                {/* Add other appointment details here */}
                <hr />
              </div>
            ))}
          </div>
        ))}
      </div>
    );
  };

  return (
    <div>
      {userFirstName && <h1>{userFirstName}'s Appointments</h1>}
      {appointments && appointments.length > 0 ? renderWeeklyCalendar() : <div>No appointments available</div>}
    </div>
  );
};

export default Appointment;
