import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import { RootState } from '../../app/store';
import getAppoint from './appointmentAPI ';
import getAppointments from './appointmentAPI ';

interface Appointment {
  therapist: {
    username: string;
  };
  patient: {
    name: string;
  };
  recurring_frequency: string;
  day_of_week?: string | null;
  time_of_day: string;
  location?: string | null;
  notes?: string | null;
  created_at: string;
  updated_at: string;
}

export interface AppointmentState {
  appointments: Appointment[];
  status: 'idle' | 'loading' | 'succeeded' | 'failed';
  error: string | null;
}

const initialState: AppointmentState = {
  appointments: [],
  status: 'idle',
  error: null,
};

export const fetchAppointments = createAsyncThunk<Appointment[], void>(
  'appointments/fetchAppointments',
  async (_, { rejectWithValue }) => {

      const appointments = await getAppointments();
      return appointments.data;

  }
);

const appointmentsSlice = createSlice({
  name: 'appointments',
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchAppointments.pending, (state) => {
        state.status = 'loading';
      })
      .addCase(fetchAppointments.fulfilled, (state, action) => {
        state.status = 'succeeded';
        state.appointments = action.payload;
      })
      .addCase(fetchAppointments.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.payload as string;
      });
  },
});

export const selectAppointments = (state: RootState) => state.appointment.appointments;
export const selectAppointmentsStatus = (state: RootState) => state.appointment.status;
export const selectAppointmentsError = (state: RootState) => state.appointment.error;

export default appointmentsSlice.reducer;
