import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import { RootState } from '../../app/store';
import getPatient from './patientAPI';
import { selectToken } from '../login/loginSlice';



export interface Ipatient {
  therapist : number;
  first_name:string;
  last_name : string;
  email: string;
  phone_number: string;
  date_of_birth: string;
  address: string;


}
export interface IpatientState {
  patients: Ipatient[];
  status: 'idle' | 'loading' | 'succeeded' | 'failed';
  error: string | null;




}
const initialState: IpatientState = {
  patients: [],
  status: 'idle',
  error: null,
};

export const fetchPatients = createAsyncThunk<Ipatient[], void>(
  "patient/fetchPatients",
  async (_, { getState }) => {
    const token = selectToken(getState() as RootState);
    if (!token) {
      throw new Error("Token not found");
    }

    const response = (await getPatient(token)) as Ipatient[];
    return response; // Return the array directly
  }
);


const patientSlice = createSlice({
  name: 'patient',
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchPatients.pending, (state) => {
        state.status = 'loading';
      })
      .addCase(fetchPatients.fulfilled, (state, action) => {
        state.status = 'succeeded';
        state.patients = action.payload.map((patient) => ({
          ...patient,
          therapist: Number(patient.therapist), // Convert therapist ID to number
        }));
      })
      .addCase(fetchPatients.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.payload as string;
      });
  },
});

// ... rest of the code remains the same


export const selectPatients = (state: RootState) => state.patient.patients;
export const selectPatientsStatus = (state: RootState) => state.patient.status;
export const selectPatientsError = (state: RootState) => state.patient.error;


export default patientSlice.reducer;
